import React, {Component} from 'react';


class AddToDo extends Component{

    render(){
        return this.props.todos.map((todo) => (
                <form style = {{display: 'flex'}}>
                    <input 
                        type='text'
                        placeholder='Add ToDo ...'
                        name='title'
                        style={{flex :'10', padding : '5px'}}
                    />
                <input
                    type = 'submit'
                    value = 'submit'
                    className = 'btn'
                    style = {{flex :'1'}}
                />    
                </form>
                

            )
            );
    }
}


export default AddToDo;
